import { Navbar, Nav, Container, NavDropdown } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import React from 'react'

function MainHeader() {
    const categorias = [
      { nombre: 'Rosas', color: '#BA68C8' },
      { nombre: 'Tulipanes', color: '#64B5F6' },
      { nombre: 'Margaritas', color: '#FFB74D' },
      { nombre: 'Orqu√≠deas', color: '#81C784' },
      { nombre: 'Ex√≥ticas', color: '#4DB6AC' }
    ];
  
    return (
      <Navbar expand="lg" style={{ 
          backgroundColor: '#F3E5F5', 
          borderBottom: '2px solid #E1BEE7'
      }}>
        <Container>
          <Navbar.Brand as={Link} to="/" style={{
              color: '#7986CB',
              fontFamily: 'Pacifico', 
              fontSize: '1.8rem',
              textShadow: '1px 1px 2px #E8EAF6'
          }}>
              üå∏ FlorPastel
          </Navbar.Brand>
          
          <Navbar.Toggle aria-controls="main-nav" />
          <Navbar.Collapse id="main-nav">
            <Nav className="ms-auto align-items-center">
              <NavDropdown 
                title={<span style={{ color: '#64B5F6' }}>Categor√≠as</span>}
                menuVariant="light"
                className="me-4"
              >
                {categorias.map((categoria, index) => (
                  <NavDropdown.Item 
                    key={index}
                    as={Link} 
                    to={`/category/${categoria.nombre.toLowerCase()}`}
                    style={{ 
                      color: categoria.color,
                      transition: 'all 0.3s ease'
                    }}
                    className="hover-categoria"
                  >
                    {categoria.nombre}
                  </NavDropdown.Item>
                ))}
              </NavDropdown>
              
              <Nav.Link 
                as={Link} 
                to="/cart" 
                style={{ 
                  color: '#4DD0E1',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '8px'
                }}
              >
                <i className="bi bi-cart3 fs-5"></i>
                <Badge bg="danger" pill>3</Badge>
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    );
  }

export default MainHeader;
