import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

function Item({ item }) {
    return (
        <Card className="h-100 shadow-sm hover-effect">
            <Card.Img
                variant="top"
                src={item.imagen}
                style={{ 
                    height: '300px', 
                    objectFit: 'cover',
                    borderBottom: '3px solidrgb(216, 131, 228)'
                }}
                alt={item.name}
            />
            <Card.Body className="d-flex flex-column">
                <Card.Title style={{ color: "#2E7D32", fontWeight: '600' }}>
                    {item.name}
                </Card.Title>
                
                {item.description && (
                    <Card.Text className="text-muted small mb-2">
                        {item.description.substring(0, 50)}...
                    </Card.Text>
                )}

                <Card.Text className="mt-auto h5" style={{ color: '#D32F2F' }}>
                    ${item.price} ARS
                </Card.Text>

                <Button
                    as={Link}
                    to={`/item/${item.id}`}
                    variant="outline-success"
                    className="mt-2 fw-bold"
                >
                    ðŸŒ¸ Ver Detalle
                </Button>
            </Card.Body>
        </Card>
    );
}

export default Item;
