import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

function Item({ item }) {
  return (
    <Card className="h-100">
      {/* 1. Corrección de la imagen */}
      <Card.Img 
        variant="top" 
        src={item.imagen} // Usamos la ruta real del producto
        style={{ height: '200px', objectFit: 'cover' }}
        alt={item.name} // Mejor accesibilidad
      />
      <Card.Body className="d-flex flex-column">
        <Card.Title>{item.name}</Card.Title>
        
        {/* 2. Corrección de la moneda */}
        <Card.Text className="mt-auto">
          {item.price}€ {/* Cambiado de ARG a € */}
        </Card.Text>
        
        {/* 3. Mejora en el botón */}
        <Button 
          as={Link} 
          to={`/item/${item.id}`} 
          variant="primary" 
          className="mt-2"
        >
          Ver Detalle
        </Button>
      </Card.Body>
    </Card>
  );
}

export default Item;
