import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

function Item({ item }) {
    return (
        <Card className="h-100">
            <Card.Img
                variant="top"
                src={item.imagen}
                style={{ height: '400px', objectFit: '400px' }}
                alt={item.name}
            />
            <Card.Body className="d-flex flex-column">
                
                    <Card.Title style={{ color: "#2E7D32" }}>{item.name}</Card.Title>

    

                <Card.Text className="mt-auto">
                    {item.price}ARG
                </Card.Text>

                <Button
                    as={Link}
                    to={`/item/${item.id}`}
                    variant="primary"
                    className="mt-2"
                >
                    Ver Detalle
                </Button>
            </Card.Body>
        </Card>
    );
}

export default Item;
