import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { products } from '../../mocks/products';
import { Container, Row, Col, Button, Badge, Alert } from 'react-bootstrap';
import { BsHeart, BsHeartFill, BsStarFill } from 'react-icons/bs';

function ItemDetailContainer() {
  const { itemId } = useParams();
  const [item, setItem] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false);
  const [selectedImage, setSelectedImage] = useState(0);

  useEffect(() => {
    const fetchItem = new Promise((resolve) => {
      setTimeout(() => {
        resolve(products.find(product => product.id === itemId));
      }, 500);
    });

    fetchItem.then((data) => {
      setItem(data);
      setIsFavorite(data?.isFavorite || false);
    });
  }, [itemId]);

  const handleAddToCart = () => {
    // LÃ³gica para agregar al carrito
    Alert.success('Producto agregado al carrito!');
  };

  if (!item) {
    return (
      <Container className="text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Cargando...</span>
        </div>
      </Container>
    );
  }

  return (
    <Container className="py-5">
      <Row className="g-5">
        {/* GalerÃ­a de imÃ¡genes */}
        <Col md={6}>
          <div className="sticky-top" style={{ top: '20px' }}>
            <img
              src={item.images[selectedImage]}
              alt={item.name}
              className="img-fluid rounded-4 shadow-lg mb-3"
              style={{ maxHeight: '70vh', objectFit: 'cover' }}
            />
            <div className="d-flex gap-2 flex-wrap">
              {item.images.map((img, index) => (
                <img
                  key={index}
                  src={img}
                  alt={`Vista ${index + 1}`}
                  className={`img-thumbnail cursor-pointer ${selectedImage === index ? 'border-primary' : ''}`}
                  style={{ width: '80px', height: '80px', objectFit: 'cover' }}
                  onClick={() => setSelectedImage(index)}
                />
              ))}
            </div>
          </div>
        </Col>

        {/* Detalles del producto */}
        <Col md={6}>
          <div className="d-flex justify-content-between align-items-start mb-4">
            <div>
              <Badge bg="success" className="mb-2">
                {item.category}
              </Badge>
              <h1 className="display-5 fw-bold mb-3">{item.name}</h1>
              <div className="d-flex align-items-center gap-2 mb-3">
                <BsStarFill className="text-warning" />
                <span className="fw-bold">{item.rating}</span>
                <span className="text-muted">({item.reviews} reseÃ±as)</span>
              </div>
            </div>
            <Button 
              variant="link" 
              onClick={() => setIsFavorite(!isFavorite)}
              className="p-0"
            >
              {isFavorite ? 
                <BsHeartFill className="text-danger" size={28} /> : 
                <BsHeart className="text-muted" size={28} />
              }
            </Button>
          </div>

          <div className="bg-light p-4 rounded-4 mb-4">
            <h2 className="text-primary mb-3">
              ${item.price} ARG
              {item.discount && (
                <small className="text-muted ms-2 text-decoration-line-through">
                  ${item.originalPrice} ARG
                </small>
              )}
            </h2>

            {item.stock > 0 ? (
              <Badge bg="info" className="mb-3">
                {item.stock} unidades disponibles
              </Badge>
            ) : (
              <Alert variant="danger" className="mb-3">
                Producto agotado
              </Alert>
            )}

            <Button 
              variant="primary" 
              size="lg" 
              className="w-100 py-3 fw-bold mb-3"
              onClick={handleAddToCart}
              disabled={item.stock === 0}
            >
              Agregar al Carrito
            </Button>

            <div className="d-flex gap-2 justify-content-center">
              <Button variant="outline-dark" size="sm">
                Compartir
              </Button>
              <Button variant="outline-dark" size="sm">
                Comparar
              </Button>
              <Button variant="outline-dark" size="sm">
                Preguntar
              </Button>
            </div>
          </div>

          {/* DescripciÃ³n y detalles */}
          <div className="mb-4">
            <h3 className="mb-3">DescripciÃ³n</h3>
            <p className="lead">{item.description}</p>
          </div>

          <div className="bg-light p-4 rounded-4">
            <h4 className="mb-3">CaracterÃ­sticas principales</h4>
            <ul className="list-unstyled">
              {item.features.map((feature, index) => (
                <li key={index} className="d-flex gap-2 mb-2">
                  <BsStarFill className="text-primary mt-1" />
                  {feature}
                </li>
              ))}
            </ul>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default ItemDetailContainer;
