function MainHeader() {
  const categorias = [
    { nombre: 'Rosas', color: '#BA68C8' },
    { nombre: 'Tulipanes', color: '#64B5F6' },
    { nombre: 'Margaritas', color: '#FFB74D' },
    { nombre: 'Orquídeas', color: '#81C784' },
    { nombre: 'Exóticas', color: '#4DB6AC' }
  ];

  return (
    <Navbar expand="lg" style={{ 
        backgroundColor: '#F3E5F5', 
        borderBottom: '2px solid #E1BEE7'
    }}>
      {/* ... resto del código ... */}
      
      <Nav.Link 
        as={Link} 
        to="/cart" 
        style={{ 
          color: '#4DD0E1',
          display: 'flex',
          alignItems: 'center',
          gap: '8px'
        }}
      >
        <i className="bi bi-cart3 fs-5"></i>
        <Badge bg="danger" pill>3</Badge> {/* Ahora está correctamente importado */}
      </Nav.Link>
      
    </Navbar>
  );
}



export default MainHeader;
