import { Link } from 'react-router-dom';
import React from 'react'

function MainHeader() {
    return (
      <Navbar expand="lg" style={{ 
          backgroundColor: '#F3E5F5', 
          borderBottom: '2px solid #E1BEE7'
      }}>
        <Container>
          <Navbar.Brand as={Link} to="/" style={{
              color: '#7986CB',
              fontFamily: 'Pacifico', 
              fontSize: '1.8rem',
              textShadow: '1px 1px 2px #E8EAF6'
          }}>
              ðŸŒ¸ FlorPastel
          </Navbar.Brand>
          
          <Navbar.Toggle aria-controls="main-nav" />
          <Navbar.Collapse id="main-nav">
            <Nav className="ms-auto">
              <NavDropdown 
                title={<span style={{ color: '#64B5F6' }}>CategorÃ­as</span>}
                menuVariant="light"
              >
                <NavDropdown.Item 
                  as={Link} 
                  to="/category/rosas"
                  style={{ color: '#BA68C8' }}
                >
                  Rosas
                </NavDropdown.Item>
                {/* Otras categorÃ­as */}
              </NavDropdown>
              
              <Nav.Link 
                as={Link} 
                to="/cart" 
                style={{ color: '#4DD0E1' }}
              >
                ðŸ›’ Carrito
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    );
  }

export default MainHeader;
